package easy;

/**
 * Created by rrr on 2017/4/15.
 * 459. Repeated Substring Pattern
 * Given a non-empty string check if it can be constructed by taking a substring of it and appending multiple copies of the substring together. You may assume the given string consists of lowercase English letters only and its length will not exceed 10000.
 * <p>
 * Example 1:
 * Input: "abab"
 * <p>
 * Output: True
 * <p>
 * Explanation: It's the substring "ab" twice.
 * Example 2:
 * Input: "aba"
 * <p>
 * Output: False
 * Example 3:
 * Input: "abcabcabcabc"
 * <p>
 * Output: True
 * <p>
 * Explanation: It's the substring "abc" four times. (And the substring "abcabc" twice.)
 */
public class RepeatedSubstringPattern {
    //23ms
    public static boolean repeatedSubstringPattern(String s) {
        for (int i = s.length() / 2; i > 0; i -= 1) {
            if ((double)s.length()/i!=(long)s.length()/i)continue;//判断是否能整除
            String sub = s.substring(0, i);
            int j;
            for (j = i; j + i <= s.length(); j += i) {
                if (!sub.equals(s.substring(j, j + i))) break;
            }
            if (j == s.length()) return true;
        }
        return false;
    }
    //24ms
    public static boolean repeatedSubstringPattern2(String s) {
        int j;
        for (int i = s.length() / 2; i > 0; i --) {
            if (s.length()%i!=0)continue;//利用求余判断是否能整除
            String sub = s.substring(0, i);
            for (j = i; j + i <= s.length(); j += i) {
                if (!sub.equals(s.substring(j, j + i))) break;
            }
            if (j == s.length()) return true;
        }
        return false;
    }
    //LeetCode解法
    //92 ms
    public static boolean repeatedSubstringPattern3(String s) {
        for (int i = s.length() / 2; i > 0; i --) {
            if (s.length()%i!=0)continue;//利用求余判断是否能整除
            StringBuilder sb=new StringBuilder();
            for (int j = 0; j  < s.length()/i; j ++) {
                sb.append(s.substring(0,i));
            }
            if (sb.toString().equals(s)) return true;
        }
        return false;
    }

    public static void main(String[] args) {
//        System.out.println(repeatedSubstringPattern3(""));
        System.out.println(repeatedSubstringPattern2("ababab"));
        System.out.println(repeatedSubstringPattern2("bacbacbac"));
        System.out.println(repeatedSubstringPattern2("aaaaaaaaa"));
    }
}
