package medium;

import java.util.HashMap;
import java.util.Map;

/**
 * Created by rrr on 2017/8/27.
 * 567. Permutation in String
 * Given two strings s1 and s2, write a function to return true if s2 contains the permutation of s1. In other words, one of the first string's permutations is the substring of the second string.
 * <p>
 * Example 1:
 * Input:s1 = "ab" s2 = "eidbaooo"
 * Output:True
 * Explanation: s2 contains one permutation of s1 ("ba").
 * Example 2:
 * Input:s1= "ab" s2 = "eidboaoo"
 * Output: False
 * Note:
 * The input strings only contain lower case letters.
 * The length of both given strings is in range [1, 10,000].
 */
public class PermutationInString {
    //63 ms beat 22%
    public boolean checkInclusion(String s1, String s2) {
        if (s1 == null || s1.length() == 0 || s2 == null) return true;
        Map<Character, Integer> map = new HashMap<>();
        for (char c : s1.toCharArray()) map.put(c, map.getOrDefault(c, 0) + 1);
        Map<Character, Integer> countMap = new HashMap<>();
        int count = 0;
        for (int i = 0; i < s2.length(); i++) {
            char c = s2.charAt(i);
            if (map.containsKey(c)) {
                countMap.put(c, countMap.getOrDefault(c, 0) + 1);
                if (countMap.get(c) <= map.get(c)) count++;
                else {
                    while (countMap.get(c) > map.get(c)) {
                        char leftChar = s2.charAt(i - count);//因为查找的字符串不允许中断，因此第一个字符就是当前位置-count（已经查找到的字符数）
                        if (countMap.containsKey(leftChar)) {
                            countMap.put(leftChar, countMap.get(leftChar) - 1);
                            if (countMap.get(leftChar) < map.get(leftChar)) count--;
                        }
                    }
                }
                if (count == s1.length()) return true;
            } else {
                countMap.clear();
                count = 0;
            }
        }

        return false;
    }

    public boolean checkInclusion2(String s1, String s2) {
        if (s1 == null || s1.length() == 0 || s2 == null) return true;
        int[] map = new int[128];
        for (char c : s1.toCharArray()) map[c]++;
        int counter = s1.length();
        for (int i = 0; i < s2.length(); i++) {
            int pos = s2.charAt(i);
            if (s1.indexOf(pos) > -1) {
                if (map[pos]-- > 0) counter--;
                while (map[pos] < 0) {
                    char leftChar = s2.charAt(i - (s1.length() - counter));//因为查找的字符串不允许中断，因此第一个字符就是当前位置-count（已经查找到的字符数）
                    if (map[leftChar]++ > 0) counter++;
                }
                if (counter == 0)
                    return true;
            } else {
                while (counter < s1.length()) {
                    char leftChar = s2.charAt(i - (s1.length() - counter));//因为查找的字符串不允许中断，因此第一个字符就是当前位置-count（已经查找到的字符数）
                    map[leftChar]++;
                    counter++;
                }
            }
        }

        return false;
    }

    public static void main(String[] args) {
//        System.out.println(new PermutationInString().checkInclusion2("vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
//                ""));
//        System.out.println(new PermutationInString().checkInclusion2("tzzerqtbp",
//                ""));
        System.out.println(new PermutationInString().checkInclusion2("abcd", "eidbaodcaodacsacdbo"));
    }
}
